),

update_records AS (
    SELECT a.ORDER_PK, a.ORDER_HASHDIFF, a.ORDERID, a.ORDERDATE, a.SHIPDATE, a.SHIPADDRESS, a.SHIPCITY, a.SHIPREGION, a.SHIPCOUNTRY, a.SHIPPOSTALCODE, a.LOAD_DATE, a.RECORD_SOURCE
